      const myHabits = habits.filter(h => h.participant === myParticipant);
      const recentWeeks = [...new Set(myHabits.map(h => h.weekStart))].sort().reverse().slice(0, 4);
      
      // Get unique habit names from recent weeks
      const uniqueHabits = new Map();
      myHabits
